import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, RouterModule } from '@angular/router';
import { Project, Material } from '../../models/project.interface';
import { ProjectService } from '../../services/project.service';
import { FormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-project-details',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  templateUrl: './project-details.component.html',
  styleUrl: './project-details.component.scss'
})
export class ProjectDetailsComponent implements OnInit {
  project: Project | null = null;
  materialTypes: string[] = [
    'Bricks', 'Steel', 'Cement', 'Glass', 'Concrete', 'Wood', 'Paint', 'Tiles',
    'Electrical Wiring', 'Plumbing Pipes', 'Drywall', 'Insulation', 'Roofing',
    'Windows', 'Doors', 'Flooring', 'HVAC Components', 'Lighting Fixtures'
  ];
  showDeleteConfirmation = false;
  showEditForm = false;
  showAddMaterialType = false;
  showAddCategory = false;
  showAddMaterialForm = false;
  showDeleteMaterialTypeConfirmation = false;
  showDeleteCategoryConfirmation = false;
  materialTypeToDelete: string | null = null;
  categoryToDelete: string | null = null;
  newMaterialType = '';
  newCategory = '';
  materialToDelete: Material | null = null;
  editingMaterial: Material | null = null;
  isLoading = false;
  errorMessage = '';

  // Pagination properties
  currentPage = 1;
  itemsPerPage = 10;
  totalItems = 0;
  totalPagesCount = 1;

  newMaterial: Material = {
    type: '',
    quantity: 0,
    unit: '',
    date: new Date().toISOString().split('T')[0],
    price: 0,
    category: 'Other'
  };

  selectedFilter = 'all';
  searchQuery = '';
  categories = ['All Materials', 'Structural', 'Finishing', 'Electrical', 'Plumbing', 'Other'];
  selectedCategory = 'All Materials';

  constructor(
    private route: ActivatedRoute,
    private projectService: ProjectService
  ) { }

  ngOnInit(): void {
    this.loadProject();
  }

  loadProject(): void {
    this.isLoading = true;
    this.errorMessage = '';
    const projectId = this.route.snapshot.paramMap.get('id');
    if (projectId) {
      this.projectService.getProject(projectId).subscribe({
        next: (project) => {
          if (project) {
            this.project = project;
            this.materialTypes = Array.from(new Set(project.materials.map(m => m.type)));
          }
          this.isLoading = false;
        },
        error: (error: unknown) => {
          this.errorMessage = 'Failed to load project details. Please try again.';
          this.isLoading = false;
        }
      });
    }
  }

  addMaterial(): void {
    if (this.project && this.newMaterial.type && this.newMaterial.quantity > 0 && this.newMaterial.unit && this.newMaterial.price > 0) {
      this.isLoading = true;
      this.errorMessage = '';
      const materialToAdd: Material = {
        ...this.newMaterial,
        date: new Date().toISOString().split('T')[0]
      };
      this.projectService.addMaterialToProject(this.project.id, materialToAdd).subscribe({
        next: (success: boolean) => {
          if (success) {
            this.newMaterial = {
              type: '',
              quantity: 0,
              unit: '',
              date: new Date().toISOString().split('T')[0],
              price: 0,
              category: 'Other'
            };
            this.loadProject();
          }
          this.isLoading = false;
        },
        error: (error: unknown) => {
          this.errorMessage = 'Failed to add material. Please try again.';
          this.isLoading = false;
        }
      });
    }
  }

  confirmDelete(material: Material): void {
    this.materialToDelete = material;
    this.showDeleteConfirmation = true;
  }

  cancelDelete(): void {
    this.showDeleteConfirmation = false;
    this.materialToDelete = null;
  }

  deleteMaterial(): void {
    if (this.project && this.materialToDelete) {
      this.isLoading = true;
      this.errorMessage = '';
      this.projectService.deleteMaterialFromProject(this.project.id, this.materialToDelete).subscribe({
        next: (success: boolean) => {
          if (success) {
            this.showDeleteConfirmation = false;
            this.materialToDelete = null;
            this.loadProject();
          }
          this.isLoading = false;
        },
        error: (error: unknown) => {
          this.errorMessage = 'Failed to delete material. Please try again.';
          this.isLoading = false;
        }
      });
    }
  }

  editMaterial(material: Material): void {
    this.editingMaterial = { ...material };
    this.showEditForm = true;
  }

  cancelEdit(): void {
    this.showEditForm = false;
    this.editingMaterial = null;
  }

  saveEdit(): void {
    if (this.project && this.editingMaterial) {
      this.isLoading = true;
      this.errorMessage = '';
      this.projectService.updateMaterialInProject(this.project.id, this.editingMaterial).subscribe({
        next: (success: boolean) => {
          if (success) {
            this.showEditForm = false;
            this.editingMaterial = null;
            this.loadProject();
          }
          this.isLoading = false;
        },
        error: (error: unknown) => {
          this.errorMessage = 'Failed to update material. Please try again.';
          this.isLoading = false;
        }
      });
    }
  }

  get paginatedMaterials(): Material[] {
    if (!this.project) return [];

    let filteredMaterials = [...this.project.materials];

    // Apply category filter
    if (this.selectedCategory !== 'All Materials') {
      filteredMaterials = filteredMaterials.filter(m => m.category === this.selectedCategory);
    }

    // Apply search filter
    if (this.searchQuery) {
      const query = this.searchQuery.toLowerCase();
      filteredMaterials = filteredMaterials.filter(m => 
        m.type.toLowerCase().includes(query) ||
        m.category.toLowerCase().includes(query)
      );
    }

    this.totalItems = filteredMaterials.length;
    this.totalPagesCount = Math.ceil(this.totalItems / this.itemsPerPage);

    // Ensure current page is valid
    if (this.currentPage > this.totalPagesCount) {
      this.currentPage = this.totalPagesCount;
    }

    const startIndex = (this.currentPage - 1) * this.itemsPerPage;
    const endIndex = startIndex + this.itemsPerPage;

    return filteredMaterials.slice(startIndex, endIndex);
  }

  onPageChange(page: number): void {
    if (page >= 1 && page <= this.totalPagesCount) {
      this.currentPage = page;
    }
  }

  get totalCost(): number {
    return this.paginatedMaterials.reduce((sum, material) => sum + (material.quantity * material.price), 0);
  }

  addNewMaterialType(): void {
    if (this.newMaterialType.trim() && !this.materialTypes.includes(this.newMaterialType.trim())) {
      this.materialTypes.push(this.newMaterialType.trim());
      this.newMaterialType = '';
      this.showAddMaterialType = false;
    }
  }

  removeMaterialType(type: string): void {
    const index = this.materialTypes.indexOf(type);
    if (index > -1) {
      this.materialTypes.splice(index, 1);
    }
  }

  onMaterialTypeChange(): void {
    if (this.newMaterial.type) {
      // You can add any additional logic here when a material type is selected
      // For example, setting default values based on the selected type
    }
  }

  addNewCategory(): void {
    if (this.newCategory.trim() && !this.categories.includes(this.newCategory.trim())) {
      this.categories.push(this.newCategory.trim());
      this.newCategory = '';
      this.showAddCategory = false;
    }
  }

  get totalPages(): number[] {
    return Array.from({ length: Math.ceil(this.totalItems / this.itemsPerPage) }, (_, i) => i + 1);
  }

  confirmDeleteMaterialType(type: string): void {
    this.materialTypeToDelete = type;
    this.showDeleteMaterialTypeConfirmation = true;
  }

  confirmDeleteCategory(category: string): void {
    this.categoryToDelete = category;
    this.showDeleteCategoryConfirmation = true;
  }

  deleteMaterialType(): void {
    if (this.materialTypeToDelete) {
      this.removeMaterialType(this.materialTypeToDelete);
      this.showDeleteMaterialTypeConfirmation = false;
      this.materialTypeToDelete = null;
    }
  }

  deleteCategory(): void {
    if (this.categoryToDelete) {
      const index = this.categories.indexOf(this.categoryToDelete);
      if (index > -1) {
        this.categories.splice(index, 1);
      }
      this.showDeleteCategoryConfirmation = false;
      this.categoryToDelete = null;
    }
  }

  cancelDeleteMaterialType(): void {
    this.showDeleteMaterialTypeConfirmation = false;
    this.materialTypeToDelete = null;
  }

  cancelDeleteCategory(): void {
    this.showDeleteCategoryConfirmation = false;
    this.categoryToDelete = null;
  }
}

<div class="project-details" *ngIf="project">
  <div class="header">
    <div class="header-left">
      <a routerLink="/dashboard" class="back-btn">
        <i class="fas fa-arrow-left"></i>
        <span>Back to Dashboard</span>
      </a>
    </div>
    <div class="header-center">
      <h1>{{ project.name }}</h1>
    </div>
    <div class="header-right">
      <div class="status-badge" [class]="project.status.toLowerCase().replace(' ', '-')">
        {{ project.status }}
      </div>
    </div>
  </div>

  <div class="info-grid">
    <div class="info-card">
      <h3>Project Details</h3>
      <p><strong>Description:</strong> {{ project.description }}</p>
      <p><strong>Location:</strong> {{ project.location }}</p>
      <p><strong>Start Date:</strong> {{ project.startDate | date }}</p>
      <p><strong>End Date:</strong> {{ project.endDate | date }}</p>
      <p><strong>Budget:</strong> ${{ project.budget | number }}</p>
    </div>

    <div class="info-card">
      <h3>Add New Material</h3>
      <div class="material-form">
        <div class="form-group">
          <label>Material Type</label>
          <div class="input-with-button">
            <div class="button-group">
              <button class="add-type-btn" (click)="showAddMaterialType = true">
                <i class="fas fa-plus"></i>
                <span>Add New Type</span>
              </button>
            </div>
            <div class="input-group">
              <select [(ngModel)]="newMaterial.type" name="type" required>
                <option value="">Select Type</option>
                <option *ngFor="let type of materialTypes" [value]="type">
                  {{ type }}
                  <button class="delete-type-btn" (click)="confirmDeleteMaterialType(type)">
                    <i class="fas fa-times"></i>
                  </button>
                </option>
              </select>
            </div>
          </div>
        </div>
        <div class="form-group">
          <label>Category</label>
          <div class="input-with-button">
            <div class="button-group">
              <button class="add-type-btn" (click)="showAddCategory = true">
                <i class="fas fa-plus"></i>
                <span>Add New Category</span>
              </button>
            </div>
            <div class="input-group">
              <select [(ngModel)]="newMaterial.category" name="category" required>
                <option value="">Select Category</option>
                <option *ngFor="let category of categories" [value]="category">
                  {{ category }}
                  <button class="delete-category-btn" (click)="confirmDeleteCategory(category)">
                    <i class="fas fa-times"></i>
                  </button>
                </option>
              </select>
            </div>
          </div>
        </div>
        <div class="form-group">
          <label for="unit">Unit</label>
          <input id="unit" type="text" [(ngModel)]="newMaterial.unit" placeholder="Enter unit (e.g., kg, pieces)">
        </div>
        <div class="form-group">
          <label for="quantity">Quantity</label>
          <input id="quantity" type="number" [(ngModel)]="newMaterial.quantity" min="0">
        </div>
        <div class="form-group">
          <label for="price">Price per Unit ($)</label>
          <input id="price" type="number" [(ngModel)]="newMaterial.price" min="0" step="0.01">
        </div>
        <div class="form-group">
          <label for="date">Date</label>
          <input id="date" type="date" [(ngModel)]="newMaterial.date">
        </div>
        <button (click)="addMaterial()" [disabled]="!newMaterial.type || !newMaterial.unit || !newMaterial.quantity || newMaterial.quantity <= 0">
          Add Material
        </button>
      </div>
    </div>
  </div>

  <div class="materials-section">
    <div class="section-header">
      <h2>Materials Log</h2>
      <div class="filters">
        <div class="search-box">
          <i class="fas fa-search"></i>
          <input 
            type="text" 
            [(ngModel)]="searchQuery" 
            placeholder="Search materials..."
            (input)="currentPage = 1"
            class="search-input"
          >
        </div>
        <div class="filter-group">
          <select [(ngModel)]="selectedCategory" (change)="currentPage = 1" class="category-select">
            <option *ngFor="let category of categories" [value]="category">
              {{ category }}
            </option>
          </select>
        </div>
      </div>
    </div>

    <div class="materials-summary">
      <div class="summary-card">
        <span class="label">Total Materials:</span>
        <span class="value">{{ totalItems }}</span>
      </div>
      <div class="summary-card">
        <span class="label">Total Cost:</span>
        <span class="value">${{ totalCost | number }}</span>
      </div>
    </div>

    <div class="error-message" *ngIf="errorMessage">
      {{ errorMessage }}
    </div>

    <div class="materials-table" [class.loading]="isLoading">
      <div class="loading-overlay" *ngIf="isLoading">
        <i class="fas fa-spinner fa-spin"></i>
      </div>

      <table>
        <thead>
          <tr>
            <th>Type</th>
            <th>Category</th>
            <th>Quantity</th>
            <th>Unit</th>
            <th>Price per Unit</th>
            <th>Total Price</th>
            <th>Date</th>
            <th class="actions">Actions</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let material of paginatedMaterials">
            <td>{{ material.type }}</td>
            <td>{{ material.category }}</td>
            <td>{{ material.quantity }}</td>
            <td>{{ material.unit }}</td>
            <td>${{ material.price | number }}</td>
            <td>${{ material.quantity * material.price | number }}</td>
            <td>{{ material.date | date }}</td>
            <td class="actions">
              <div class="action-buttons">
                <button class="edit-btn" (click)="editMaterial(material)">
                  <i class="fas fa-edit"></i>
                </button>
                <button class="delete-btn" (click)="confirmDelete(material)">
                  <i class="fas fa-trash"></i>
                </button>
              </div>
            </td>
          </tr>
        </tbody>
      </table>

      <!-- Add pagination controls -->
      <div class="pagination">
        <button class="page-btn" (click)="onPageChange(currentPage - 1)" [disabled]="currentPage === 1">
          <i class="fas fa-chevron-left"></i>
        </button>
        <div class="page-numbers">
          <button 
            *ngFor="let page of totalPages" 
            class="page-btn" 
            [class.active]="page === currentPage"
            (click)="onPageChange(page)">
            {{ page }}
          </button>
        </div>
        <button class="page-btn" (click)="onPageChange(currentPage + 1)" [disabled]="currentPage === totalPagesCount">
          <i class="fas fa-chevron-right"></i>
        </button>
      </div>
    </div>
  </div>
</div>

<!-- Edit Material Form -->
<div class="edit-material-form" *ngIf="showEditForm && editingMaterial">
  <div class="form-content">
    <h3>Edit Material</h3>
    <div class="form-group">
      <label for="editType">Material Type</label>
      <input id="editType" type="text" [(ngModel)]="editingMaterial!.type" placeholder="Enter material type">
    </div>
    <div class="form-group">
      <label for="editCategory">Category</label>
      <select id="editCategory" [(ngModel)]="editingMaterial!.category">
        <option *ngFor="let category of categories" [value]="category" [disabled]="category === 'All Materials'">
          {{ category }}
        </option>
      </select>
    </div>
    <div class="form-group">
      <label for="editUnit">Unit</label>
      <input id="editUnit" type="text" [(ngModel)]="editingMaterial!.unit" placeholder="Enter unit">
    </div>
    <div class="form-group">
      <label for="editQuantity">Quantity</label>
      <input id="editQuantity" type="number" [(ngModel)]="editingMaterial!.quantity" min="0">
    </div>
    <div class="form-group">
      <label for="editPrice">Price per Unit ($)</label>
      <input id="editPrice" type="number" [(ngModel)]="editingMaterial!.price" min="0" step="0.01">
    </div>
    <div class="form-group">
      <label for="editDate">Date</label>
      <input id="editDate" type="date" [(ngModel)]="editingMaterial!.date">
    </div>
    <div class="form-actions">
      <button class="cancel-btn" (click)="cancelEdit()">Cancel</button>
      <button class="save-btn" (click)="saveEdit()" [disabled]="!editingMaterial!.type || !editingMaterial!.unit || !editingMaterial!.quantity || editingMaterial!.quantity <= 0">
        Save Changes
      </button>
    </div>
  </div>
</div>

<!-- Delete Confirmation Dialog -->
<div class="confirmation-dialog" *ngIf="showDeleteConfirmation">
  <div class="dialog-content">
    <h3>Confirm Delete</h3>
    <p>Are you sure you want to delete this material?</p>
    <div class="dialog-actions">
      <button class="cancel-btn" (click)="cancelDelete()">Cancel</button>
      <button class="confirm-btn" (click)="deleteMaterial()" [disabled]="isLoading">
        Delete
      </button>
    </div>
  </div>
</div>

<!-- Add Material Type Form -->
<div class="add-material-type-form" *ngIf="showAddMaterialType">
  <div class="form-content">
    <h3>Add New Material Type</h3>
    <div class="form-group">
      <label>Material Type Name</label>
      <div class="input-with-button">
        <input type="text" [(ngModel)]="newMaterialType" placeholder="Enter material type name">
        <button class="add-btn" (click)="addNewMaterialType()" [disabled]="!newMaterialType">
          <i class="fas fa-plus"></i>
          Add
        </button>
      </div>
    </div>
    <div class="form-actions">
      <button class="cancel-btn" (click)="showAddMaterialType = false">
        <i class="fas fa-times"></i>
        Cancel
      </button>
    </div>
  </div>
</div>

<!-- Add Category Form -->
<div class="add-material-type-form" *ngIf="showAddCategory">
  <div class="form-content">
    <h3>Add New Category</h3>
    <div class="form-group">
      <label>Category Name</label>
      <div class="input-with-button">
        <input type="text" [(ngModel)]="newCategory" placeholder="Enter category name">
        <button class="add-btn" (click)="addNewCategory()" [disabled]="!newCategory">
          <i class="fas fa-plus"></i>
          Add
        </button>
      </div>
    </div>
    <div class="form-actions">
      <button class="cancel-btn" (click)="showAddCategory = false">
        <i class="fas fa-times"></i>
        Cancel
      </button>
    </div>
  </div>
</div>

<!-- Delete Material Type Confirmation Dialog -->
<div class="confirmation-dialog" *ngIf="showDeleteMaterialTypeConfirmation">
  <div class="dialog-content">
    <h3>Confirm Delete</h3>
    <p>Are you sure you want to delete the material type "{{ materialTypeToDelete }}"?</p>
    <div class="dialog-actions">
      <button class="cancel-btn" (click)="cancelDeleteMaterialType()">Cancel</button>
      <button class="confirm-btn" (click)="deleteMaterialType()">Delete</button>
    </div>
  </div>
</div>

<!-- Delete Category Confirmation Dialog -->
<div class="confirmation-dialog" *ngIf="showDeleteCategoryConfirmation">
  <div class="dialog-content">
    <h3>Confirm Delete</h3>
    <p>Are you sure you want to delete the category "{{ categoryToDelete }}"?</p>
    <div class="dialog-actions">
      <button class="cancel-btn" (click)="cancelDeleteCategory()">Cancel</button>
      <button class="confirm-btn" (click)="deleteCategory()">Delete</button>
    </div>
  </div>
</div>
