import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router, RouterModule } from '@angular/router';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  templateUrl: './login.component.html',
  styleUrl: './login.component.scss'
})
export class LoginComponent implements OnInit {
  loginData = {
    email: '',
    password: '',
    rememberMe: false
  };

  isLoading = false;
  showPassword = false;
  errorMessage = '';
  successMessage = '';

  constructor(private router: Router) { }

  ngOnInit(): void {
    // Check if user is already logged in
    const isLoggedIn = localStorage.getItem('isLoggedIn');
    if (isLoggedIn === 'true') {
      this.router.navigate(['/dashboard']);
    }
  }

  togglePasswordVisibility(): void {
    this.showPassword = !this.showPassword;
  }

  login(): void {
    // Reset messages
    this.errorMessage = '';
    this.successMessage = '';

    // Validate form
    if (!this.loginData.email || !this.loginData.password) {
      this.errorMessage = 'Please enter both email and password';
      return;
    }

    // Show loading state
    this.isLoading = true;

    // Simulate API call
    setTimeout(() => {
      // For demo purposes, accept any email/password combination
      // In a real app, you would validate against your backend
      if (this.loginData.email && this.loginData.password) {
        // Store login state
        localStorage.setItem('isLoggedIn', 'true');
        if (this.loginData.rememberMe) {
          localStorage.setItem('userEmail', this.loginData.email);
        }

        // Show success message
        this.successMessage = 'Login successful! Redirecting...';

        // Redirect to dashboard after a short delay
        setTimeout(() => {
          this.router.navigate(['/dashboard']);
        }, 1000);
      } else {
        this.errorMessage = 'Invalid email or password';
      }

      this.isLoading = false;
    }, 1500);
  }

  forgotPassword(): void {
    // In a real app, this would navigate to a password reset page
    alert('Password reset functionality would be implemented here');
  }
}
<div class="login-container">
  <div class="login-card">
    <div class="login-header">
      <div class="logo">
        <i class="fas fa-building"></i>
        <h1>Construction Manager</h1>
      </div>
      <p class="tagline">Manage your construction projects with ease</p>
    </div>

    <div class="login-form">
      <div class="form-group">
        <label for="email">Email Address</label>
        <div class="input-with-icon">
          <i class="fas fa-envelope"></i>
          <input 
            id="email" 
            type="email" 
            [(ngModel)]="loginData.email" 
            placeholder="Enter your email"
            [class.error]="errorMessage && !loginData.email"
          >
        </div>
      </div>

      <div class="form-group">
        <label for="password">Password</label>
        <div class="input-with-icon">
          <i class="fas fa-lock"></i>
          <input 
            id="password" 
            [type]="showPassword ? 'text' : 'password'" 
            [(ngModel)]="loginData.password" 
            placeholder="Enter your password"
            [class.error]="errorMessage && !loginData.password"
          >
          <button 
            class="toggle-password" 
            (click)="togglePasswordVisibility()"
            type="button"
          >
            <i class="fas" [class.fa-eye]="!showPassword" [class.fa-eye-slash]="showPassword"></i>
          </button>
        </div>
      </div>

      <div class="form-options">
        <label class="remember-me">
          <input 
            type="checkbox" 
            [(ngModel)]="loginData.rememberMe"
          >
          <span>Remember me</span>
        </label>
        <a href="#" class="forgot-password" (click)="forgotPassword()">Forgot password?</a>
      </div>

      <div class="alert error" *ngIf="errorMessage">
        <i class="fas fa-exclamation-circle"></i>
        <span>{{ errorMessage }}</span>
      </div>

      <div class="alert success" *ngIf="successMessage">
        <i class="fas fa-check-circle"></i>
        <span>{{ successMessage }}</span>
      </div>

      <button 
        class="login-btn" 
        (click)="login()" 
        [disabled]="isLoading"
      >
        <i class="fas fa-spinner fa-spin" *ngIf="isLoading"></i>
        <span *ngIf="!isLoading">Sign In</span>
        <span *ngIf="isLoading">Signing in...</span>
      </button>
    </div>

    <div class="login-footer">
      <p>Don't have an account? <a href="#">Contact your administrator</a></p>
    </div>
  </div>

  <div class="login-background">
    <div class="background-overlay"></div>
    <div class="background-image"></div>
  </div>
</div>
